/* CSS Reset */
*, *::before, *::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

/* CSS Variables */
:root {
  /* Colors */
  --color-background: #1c1c1e;
  --color-calculator-bg: #2d2d30;
  --color-display-bg: #1c1c1e;
  --color-display-text: #ffffff;
  --color-digit-bg: #505050;
  --color-digit-text: #ffffff;
  --color-operator-bg: #ff9f0a;
  --color-operator-text: #ffffff;
  --color-function-bg: #a5a5a5;
  --color-function-text: #000000;
  --color-operator-active: #ffc966;

  /* Spacing */
  --spacing-xs: 4px;
  --spacing-sm: 8px;
  --spacing-md: 12px;
  --spacing-lg: 16px;
  --spacing-xl: 24px;

  /* Fonts */
  --font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, sans-serif;
  --font-size-display: 48px;
  --font-size-display-small: 36px;
  --font-size-button: 28px;
  --font-size-previous: 16px;

  /* Borders */
  --border-radius-sm: 8px;
  --border-radius-md: 12px;
  --border-radius-lg: 24px;

  /* Shadows */
  --shadow-calculator: 0 10px 40px rgba(0, 0, 0, 0.4);

  /* Transitions */
  --transition-fast: 100ms;
  --transition-normal: 150ms;
}

/* Dark mode colors (default) */
:root {
  color-scheme: dark;
}

/* Light mode support */
@media (prefers-color-scheme: light) {
  :root {
    --color-background: #f2f2f7;
    --color-calculator-bg: #ffffff;
    --color-display-bg: #f2f2f7;
    --color-display-text: #000000;
    --color-digit-bg: #e0e0e0;
    --color-digit-text: #000000;
    --color-function-bg: #d4d4d4;
    --color-function-text: #000000;
    --shadow-calculator: 0 10px 40px rgba(0, 0, 0, 0.15);
  }
}

html {
  font-size: 16px;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body {
  font-family: var(--font-family);
  background-color: var(--color-background);
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
}

#root {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  min-height: 100vh;
  padding: var(--spacing-lg);
}

button {
  font-family: inherit;
  border: none;
  outline: none;
  cursor: pointer;
}

button:focus-visible {
  outline: 2px solid var(--color-operator-bg);
  outline-offset: 2px;
}

