export type Operator = '+' | '-' | '×' | '÷' | null;

export interface CalculatorState {
  displayValue: string;
  previousValue: string | null;
  operator: Operator;
  waitingForOperand: boolean;
  lastOperand: string | null;
  lastOperator: Operator;
}

export type CalculatorAction =
  | { type: 'INPUT_DIGIT'; digit: string }
  | { type: 'INPUT_DECIMAL' }
  | { type: 'SET_OPERATOR'; operator: Operator }
  | { type: 'CALCULATE' }
  | { type: 'CLEAR' }
  | { type: 'TOGGLE_SIGN' }
  | { type: 'PERCENT' }
  | { type: 'BACKSPACE' };

export type ButtonVariant = 'digit' | 'operator' | 'function' | 'equals';

export interface ButtonProps {
  label: string;
  onClick: () => void;
  variant?: ButtonVariant;
  testId: string;
  ariaLabel?: string;
  isActive?: boolean;
  wide?: boolean;
}

