import { ButtonProps } from '../types/calculator';
import './Button.css';

export function Button({
  label,
  onClick,
  variant = 'digit',
  testId,
  ariaLabel,
  isActive = false,
  wide = false
}: ButtonProps) {
  const className = [
    'calculator-button',
    `calculator-button--${variant}`,
    isActive ? 'calculator-button--active' : '',
    wide ? 'calculator-button--wide' : ''
  ].filter(Boolean).join(' ');

  return (
    <button
      className={className}
      onClick={onClick}
      data-testid={testId}
      aria-label={ariaLabel || label}
    >
      {label}
    </button>
  );
}

