.calculator-button--operator {
  background-color: var(--color-operator-bg);
  color: var(--color-operator-text);
}

.calculator-button--operator.calculator-button--active {
  background-color: var(--color-operator-active);
  color: var(--color-operator-bg);
}

/* Function buttons (C, +/-, %) */
.calculator-button--function {
  background-color: var(--color-function-bg);
  color: var(--color-function-text);
}

/* Equals button */
.calculator-button--equals {
  background-color: var(--color-operator-bg);
  color: var(--color-operator-text);
}

/* Wide button (0) */
.calculator-button--wide {
  grid-column: span 2;
  justify-content: flex-start;
  padding-left: 28px;
}

