input.content:
import './Display.css';

interface DisplayProps {
  value: string;
  previousOperand?: string | null;
  operator?: string | null;
}

function formatNumber(value: string): string {
  // Handle special cases
  if (value === 'Error' || value === 'Infinity' || value === '-Infinity') {
    return value;
  }

  // Don't format if user is still typing a decimal
  if (value.endsWith('.')) {
    const [intPart] = value.split('.');
    const formattedInt = parseInt(intPart, 10).toLocaleString('en-US');
    return formattedInt + '.';
  }

  // Parse the number
  const num = parseFloat(value);
  if (isNaN(num)) return value;

  // Split into integer and decimal parts
  const [intPart, decPart] = value.split('.');

  // Format integer part with commas
  const formattedInt = parseInt(intPart, 10).toLocaleString('en-US');

  // If there's a decimal part, append it
  if (decPart !== undefined) {
    return formattedInt + '.' + decPart;
  }

  return formattedInt;
}

function getFontSize(value: string): string {
  const length = value.replace(/,/g, '').length;
  if (length > 12) return 'var(--font-size-display-small)';
  if (length > 9) return 'calc(var(--font-size-display) * 0.85)';
  return 'var(--font-size-display)';
}

export function Display({ value, previousOperand, operator }: DisplayProps) {
  const formattedValue = formatNumber(value);
  const fontSize = getFontSize(value);

  return (
    <div className="calculator-display" data-testid="display" role="status" aria-live="polite">
      {(previousOperand || operator) && (
        <div className="calculator-display__previous" data-testid="previous-operand">
          {previousOperand ? formatNumber(previousOperand) : ''} {operator || ''}
        </div>
      )}
      <div
        className="calculator-display__value"
        style={{ fontSize }}
      >
        {formattedValue}
      </div>
      {operator && (
        <div className="calculator-display__indicator" data-testid="operation-indicator">
          {operator}
        </div>
      )}
    </div>
  );
}

