import { useState, useCallback, useEffect } from 'react';
import { Operator, CalculatorState } from '../types/calculator';

const MAX_DIGITS = 15;

const initialState: CalculatorState = {
  displayValue: '0',
  previousValue: null,
  operator: null,
  waitingForOperand: false,
  lastOperand: null,
  lastOperator: null,
};

function roundResult(value: number): number {
  // Handle floating point precision issues
  return Math.round(value * 1e12) / 1e12;
}

function calculate(left: number, right: number, operator: Operator): number | 'Error' {
  switch (operator) {
    case '+':
      return roundResult(left + right);
    case '-':
      return roundResult(left - right);
    case '×':
      return roundResult(left * right);
    case '÷':
      if (right === 0) return 'Error';
      return roundResult(left / right);
    default:
      return right;
  }
}

export function useCalculator() {
  const [state, setState] = useState<CalculatorState>(initialState);
  const [activeKey, setActiveKey] = useState<string | null>(null);

  const inputDigit = useCallback((digit: string) => {
    setState((prev) => {
      if (prev.displayValue === 'Error') {
        return { ...prev, displayValue: digit, waitingForOperand: false };
      }

      if (prev.waitingForOperand) {
        return {
          ...prev,
          displayValue: digit,
          waitingForOperand: false,
        };
      }

      // Check max digits (excluding decimal point)
      const currentDigits = prev.displayValue.replace('.', '').replace('-', '');
      if (currentDigits.length >= MAX_DIGITS) {
        return prev;
      }

      // Handle leading zero
      if (prev.displayValue === '0' && digit !== '0') {
        return { ...prev, displayValue: digit };
      }

      // Prevent multiple leading zeros
      if (prev.displayValue === '0' && digit === '0') {
        return prev;
      }

      return { ...prev, displayValue: prev.displayValue + digit };
    });
  }, []);

  const inputDecimal = useCallback(() => {
    setState((prev) => {
      if (prev.displayValue === 'Error') {
        return { ...prev, displayValue: '0.', waitingForOperand: false };
      }

      if (prev.waitingForOperand) {
        return {
          ...prev,
          displayValue: '0.',
          waitingForOperand: false,
        };
      }

      // Don't add another decimal if one exists
      if (prev.displayValue.includes('.')) {
        return prev;
      }

      // Check max digits
      const currentDigits = prev.displayValue.replace('.', '').replace('-', '');
      if (currentDigits.length >= MAX_DIGITS) {
        return prev;
      }

      return { ...prev, displayValue: prev.displayValue + '.' };
    });
  }, []);

  const setOperator = useCallback((nextOperator: Operator) => {
    setState((prev) => {
      if (prev.displayValue === 'Error') {
        return prev;
      }

      const inputValue = parseFloat(prev.displayValue);

      // If we already have an operator and are not waiting for operand,
      // calculate the intermediate result (chained operations)
      if (prev.operator && prev.previousValue !== null && !prev.waitingForOperand) {
        const prevValue = parseFloat(prev.previousValue);
        const result = calculate(prevValue, inputValue, prev.operator);

        if (result === 'Error') {
          return {
            ...initialState,
            displayValue: 'Error',
          };
        }

        const resultStr = String(result);
        return {
          ...prev,
          displayValue: resultStr,
          previousValue: resultStr,
          operator: nextOperator,
          waitingForOperand: true,
        };
      }

      // Just changing the operator (no calculation needed)
      return {
        ...prev,
        previousValue: prev.displayValue,
        operator: nextOperator,
        waitingForOperand: true,
      };
    });
  }, []);

  const performCalculation = useCallback(() => {
    setState((prev) => {
      if (prev.displayValue === 'Error') {
        return prev;
      }

      const inputValue = parseFloat(prev.displayValue);

      // Handle repeated equals - use last operand and operator
      if (!prev.operator && prev.lastOperator && prev.lastOperand !== null) {
        const lastOperand = parseFloat(prev.lastOperand);
        const result = calculate(inputValue, lastOperand, prev.lastOperator);

        if (result === 'Error') {
          return {
            ...initialState,
            displayValue: 'Error',
          };
        }

        return {
          ...prev,
          displayValue: String(result),
        };
      }

      // No operator set, nothing to calculate
      if (!prev.operator || prev.previousValue === null) {
        return prev;
      }

      const prevValue = parseFloat(prev.previousValue);
      const result = calculate(prevValue, inputValue, prev.operator);

      if (result === 'Error') {
        return {
          ...initialState,
          displayValue: 'Error',
        };
      }

      return {
        displayValue: String(result),
        previousValue: null,
        operator: null,
        waitingForOperand: true,
        lastOperand: prev.displayValue,
        lastOperator: prev.operator,
      };
    });
  }, []);

  const clear = useCallback(() => {
    setState(initialState);
  }, []);

  const toggleSign = useCallback(() => {
    setState((prev) => {
      if (prev.displayValue === 'Error' || prev.displayValue === '0') {
        return prev;
      }

      const newValue = prev.displayValue.startsWith('-')
        ? prev.displayValue.slice(1)
        : '-' + prev.displayValue;

      return { ...prev, displayValue: newValue };
    });
  }, []);

  const percent = useCallback(() => {
    setState((prev) => {
      if (prev.displayValue === 'Error') {
        return prev;
      }

      const value = parseFloat(prev.displayValue);

      // If we have an operator and previous value, calculate percentage of that value
      // e.g., 100 + 10% = 100 + 10 = 110
      if (prev.operator && prev.previousValue !== null) {
        const prevValue = parseFloat(prev.previousValue);
        const percentValue = roundResult((prevValue * value) / 100);
        return { ...prev, displayValue: String(percentValue) };
      }

      // Otherwise just divide by 100
      const percentValue = roundResult(value / 100);
      return { ...prev, displayValue: String(percentValue) };
    });
  }, []);

  const backspace = useCallback(() => {
    setState((prev) => {
      if (prev.displayValue === 'Error' || prev.waitingForOperand) {
        return prev;
      }

      if (prev.displayValue.length === 1 || (prev.displayValue.length === 2 && prev.displayValue.startsWith('-'))) {
        return { ...prev, displayValue: '0' };
      }

      return { ...prev, displayValue: prev.displayValue.slice(0, -1) };
    });
  }, []);

  // Keyboard support
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      // Prevent default for calculator keys
      if (
        /^[0-9.]$/.test(e.key) ||
        ['+', '-', '*', '/', 'Enter', '=', 'Escape', 'Delete', 'Backspace'].includes(e.key)
      ) {
        e.preventDefault();
      }

      // Digit keys
      if (/^[0-9]$/.test(e.key)) {
        setActiveKey(`btn-${e.key}`);
        inputDigit(e.key);
        return;
      }

      // Decimal point
      if (e.key === '.') {
        setActiveKey('btn-decimal');
        inputDecimal();
        return;
      }

      // Operators
      if (e.key === '+') {
        setActiveKey('btn-add');
        setOperator('+');
        return;
      }
      if (e.key === '-') {
        setActiveKey('btn-subtract');
        setOperator('-');
        return;
      }
      if (e.key === '*') {
        setActiveKey('btn-multiply');
        setOperator('×');
        return;
      }
      if (e.key === '/') {
        setActiveKey('btn-divide');
        setOperator('÷');
        return;
      }

      // Calculate
      if (e.key === 'Enter' || e.key === '=') {
        setActiveKey('btn-equals');
        performCalculation();
        return;
      }

      // Clear
      if (e.key === 'Escape' || e.key === 'Delete') {
        setActiveKey('btn-clear');
        clear();
        return;
      }

      // Backspace
      if (e.key === 'Backspace') {
        setActiveKey('btn-backspace');
        backspace();
        return;
      }
    };

    const handleKeyUp = () => {
      setActiveKey(null);
    };

    document.addEventListener('keydown', handleKeyDown);
    document.addEventListener('keyup', handleKeyUp);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('keyup', handleKeyUp);
    };
  }, [inputDigit, inputDecimal, setOperator, performCalculation, clear, backspace]);

  return {
    displayValue: state.displayValue,
    previousValue: state.previousValue,
    operator: state.operator,
    activeKey,
    inputDigit,
    inputDecimal,
    setOperator,
    calculate: performCalculation,
    clear,
    toggleSign,
    percent,
    backspace,
  };
}

