import { useCalculator } from '../hooks/useCalculator';
import { Display } from './Display';
import { ButtonGrid } from './ButtonGrid';
import { Button } from './Button';
import { Operator } from '../types/calculator';
import './Calculator.css';

export function Calculator() {
  const {
    displayValue,
    previousValue,
    operator,
    activeKey,
    inputDigit,
    inputDecimal,
    setOperator,
    calculate,
    clear,
    toggleSign,
    percent,
    backspace,
  } = useCalculator();

  const isOperatorActive = (op: Operator) => operator === op && displayValue !== 'Error';

  const isKeyActive = (testId: string) => activeKey === testId;

  return (
    <div className="calculator" data-testid="calculator">
      <Display
        value={displayValue}
        previousOperand={previousValue}
        operator={operator}
      />
      <ButtonGrid>
        {/* Row 1: C, +/-, %, ÷ */}
        <Button
          label="C"
          onClick={clear}
          variant="function"
          testId="btn-clear"
          ariaLabel="Clear"
          isActive={isKeyActive('btn-clear')}
        />
        <Button
          label="+/−"
          onClick={toggleSign}
          variant="function"
          testId="btn-sign"
          ariaLabel="Toggle sign"
          isActive={isKeyActive('btn-sign')}
        />
        <Button
          label="%"
          onClick={percent}
          variant="function"
          testId="btn-percent"
          ariaLabel="Percent"
          isActive={isKeyActive('btn-percent')}
        />
        <Button
          label="÷"
          onClick={() => setOperator('÷')}
          variant="operator"
          testId="btn-divide"
          ariaLabel="Divide"
          isActive={isOperatorActive('÷') || isKeyActive('btn-divide')}
        />

        {/* Row 2: 7, 8, 9, × */}
        <Button
          label="7"
          onClick={() => inputDigit('7')}
          variant="digit"
          testId="btn-7"
          isActive={isKeyActive('btn-7')}
        />
        <Button
          label="8"
          onClick={() => inputDigit('8')}
          variant="digit"
          testId="btn-8"
          isActive={isKeyActive('btn-8')}
        />
        <Button
          label="9"
          onClick={() => inputDigit('9')}
          variant="digit"
          testId="btn-9"
          isActive={isKeyActive('btn-9')}
        />
        <Button
          label="×"
          onClick={() => setOperator('×')}
          variant="operator"
          testId="btn-multiply"
          ariaLabel="Multiply"
          isActive={isOperatorActive('×') || isKeyActive('btn-multiply')}
        />

        {/* Row 3: 4, 5, 6, - */}
        <Button
          label="4"
          onClick={() => inputDigit('4')}
          variant="digit"
          testId="btn-4"
          isActive={isKeyActive('btn-4')}
        />
        <Button
          label="5"
          onClick={() => inputDigit('5')}
          variant="digit"
          testId="btn-5"
          isActive={isKeyActive('btn-5')}
        />
        <Button
          label="6"
          onClick={() => inputDigit('6')}
          variant="digit"
          testId="btn-6"
          isActive={isKeyActive('btn-6')}
        />
        <Button
          label="−"
          onClick={() => setOperator('-')}
          variant="operator"
          testId="btn-subtract"
          ariaLabel="Subtract"
          isActive={isOperatorActive('-') || isKeyActive('btn-subtract')}
        />

        {/* Row 4: 1, 2, 3, + */}
        <Button
          label="1"
          onClick={() => inputDigit('1')}
          variant="digit"
          testId="btn-1"
          isActive={isKeyActive('btn-1')}
        />
        <Button
          label="2"
          onClick={() => inputDigit('2')}
          variant="digit"
          testId="btn-2"
          isActive={isKeyActive('btn-2')}
        />
        <Button
          label="3"
          onClick={() => inputDigit('3')}
          variant="digit"
          testId="btn-3"
          isActive={isKeyActive('btn-3')}
        />
        <Button
          label="+"
          onClick={() => setOperator('+')}
          variant="operator"
          testId="btn-add"
          ariaLabel="Add"
          isActive={isOperatorActive('+') || isKeyActive('btn-add')}
        />

        {/* Row 5: 0 (wide), ., = */}
        <Button
          label="0"
          onClick={() => inputDigit('0')}
          variant="digit"
          testId="btn-0"
          wide
          isActive={isKeyActive('btn-0')}
        />
        <Button
          label="."
          onClick={inputDecimal}
          variant="digit"
          testId="btn-decimal"
          ariaLabel="Decimal point"
          isActive={isKeyActive('btn-decimal')}
        />
        <Button
          label="="
          onClick={calculate}
          variant="equals"
          testId="btn-equals"
          ariaLabel="Equals"
          isActive={isKeyActive('btn-equals')}
        />
      </ButtonGrid>
    </div>
  );
}

