          variant="digit"
          testId="btn-3"
          isActive={isKeyActive('btn-3')}
        />
        <Button
          label="+"
          onClick={() => setOperator('+')}
          variant="operator"
          testId="btn-add"
          ariaLabel="Add"
          isActive={isOperatorActive('+') || isKeyActive('btn-add')}
        />

        {/* Row 5: 0 (wide), ., = */}
        <Button
          label="0"
