import { describe, it, expect } from 'vitest';

// Test the calculation logic directly
function roundResult(value: number): number {
  return Math.round(value * 1e12) / 1e12;
}

function calculate(left: number, right: number, operator: '+' | '-' | '×' | '÷' | null): number | 'Error' {
  switch (operator) {
    case '+':
      return roundResult(left + right);
    case '-':
      return roundResult(left - right);
    case '×':
      return roundResult(left * right);
    case '÷':
      if (right === 0) return 'Error';
      return roundResult(left / right);
    default:
      return right;
  }
}

describe('Calculator utility functions', () => {
  describe('Addition', () => {
    it('should add two positive integers', () => {
      expect(calculate(2, 3, '+')).toBe(5);
    });

    it('should add decimal numbers correctly', () => {
      expect(calculate(1.5, 2.5, '+')).toBe(4);
    });

    it('should handle negative numbers', () => {
      expect(calculate(-5, 3, '+')).toBe(-2);
    });

    it('should handle floating point precision (0.1 + 0.2)', () => {
      expect(calculate(0.1, 0.2, '+')).toBe(0.3);
    });
  });

  describe('Subtraction', () => {
    it('should subtract two numbers', () => {
      expect(calculate(5, 3, '-')).toBe(2);
    });

    it('should work with decimals', () => {
      expect(calculate(5.5, 2.2, '-')).toBe(3.3);
    });

    it('should produce negative results', () => {
      expect(calculate(3, 5, '-')).toBe(-2);
    });
  });

  describe('Multiplication', () => {
    it('should multiply two numbers', () => {
      expect(calculate(4, 3, '×')).toBe(12);
    });

    it('should work with decimals', () => {
      expect(calculate(2.5, 4, '×')).toBe(10);
    });

    it('should handle multiplication by zero', () => {
      expect(calculate(5, 0, '×')).toBe(0);
    });
  });

  describe('Division', () => {
    it('should divide two numbers', () => {
      expect(calculate(8, 2, '÷')).toBe(4);
    });

    it('should work with decimals', () => {
      expect(calculate(7.5, 2.5, '÷')).toBe(3);
    });

    it('should return Error for division by zero', () => {
      expect(calculate(8, 0, '÷')).toBe('Error');
    });
  });

  describe('Floating point precision', () => {
    it('should handle 0.1 + 0.2 = 0.3 correctly', () => {
      expect(calculate(0.1, 0.2, '+')).toBe(0.3);
    });

    it('should handle 0.3 - 0.1 = 0.2 correctly', () => {
      expect(calculate(0.3, 0.1, '-')).toBe(0.2);
    });

    it('should handle 0.1 * 0.2 = 0.02 correctly', () => {
      expect(calculate(0.1, 0.2, '×')).toBe(0.02);
    });
  });
});

