    });

    it('should return Error for division by zero', () => {
      expect(calculate(8, 0, '÷')).toBe('Error');
    });
  });

  describe('Floating point precision', () => {
    it('should handle 0.1 + 0.2 = 0.3 correctly', () => {
      expect(calculate(0.1, 0.2, '+')).toBe(0.3);
    });

    it('should handle 0.3 - 0.1 = 0.2 correctly', () => {
      expect(calculate(0.3, 0.1, '-')).toBe(0.2);
    });

    it('should handle 0.1 * 0.2 = 0.02 correctly', () => {
      expect(calculate(0.1, 0.2, '×')).toBe(0.02);
